<?php

require_once __DIR__.'/../libs/traits.php';  // Allgemeine Funktionen

class PollenCount extends IPSModule
{
    use TimerHelper, DebugHelper;

    // JSON Daten URL
    private $dataURL = 'https://opendata.dwd.de/climate_environment/health/alerts/s31fg.json';
    // Jahreskalender URL
    private $calURL = 'https://www.wetterdienst.de/imgs/pollenflugkalendar.jpg';

    /**
     * Create.
     */
    public function Create()
    {
        //Never delete this line!
        parent::Create();

        $this->RegisterPropertyInteger('State', '10');
        $this->RegisterPropertyInteger('Region', '10');
        $this->RegisterPropertyInteger('Days', 2);
        $this->RegisterPropertyBoolean('CreateHint', true);
        $this->RegisterPropertyBoolean('CreateForecast', true);
        $this->RegisterPropertyBoolean('CreateLink', true);
        $this->RegisterPropertyBoolean('DailyUpdate', true);
        // Register daily update timer
        $this->RegisterTimer('UpdateTimer', 0, 'POLLEN_Update('.$this->InstanceID.');');
    }

    /**
    * Configuration Form.
    *
    * @return JSON configuration string.
    */
    public function GetConfigurationForm()
    {
        $form = json_decode(file_get_contents(__DIR__ . '/form.json'), true);
        return json_encode($form);
    }

    /**
     * Apply Configuration Changes.
     */
    public function ApplyChanges()
    {
        //Never delete this line!
        parent::ApplyChanges();
        // Selections
        $state = $this->ReadPropertyInteger('State');
        $region = $this->ReadPropertyInteger('Region');
        $days = $this->ReadPropertyInteger('Days');
        // Creations
        $hint = $this->ReadPropertyBoolean('CreateHint');
        $forecast = $this->ReadPropertyBoolean('CreateForecast');
        $link = $this->ReadPropertyBoolean('CreateLink');
        // Variables
        $this->RegisterVariableInteger('LastUpdate', 'Letzte Aktualisierung', '~UnixTimestamp', 0);
        $this->RegisterVariableInteger('NextUpdate', 'Nächste Aktualisierung', '~UnixTimestamp', 1);
        $this->MaintainVariable('Hint', 'Tageshinweis', vtString, '', 2, $hint);
        $this->MaintainVariable('Forecast', 'Vorhersage', vtString, '~HTMLBox', 2, $forecast);
        $this->MaintainVariable('Link', 'Jahreskalender', vtString, '~HTMLBox', 2, $link);
        // Static content (link to image)
        if ($link == true) {
            $this->SetValueString('Link', '<img src="'.static::$calURL.'" style="max-height: 100%; max-width: 100%;" \>');
        }
        // Daily Update Calculate next update interval
        $update = $this->ReadPropertyBoolean('DailyUpdate');
        if ($update == true) {
            // DWD renew data always 11 o'clock
            $this->UpdateTimerInterval('UpdateTimer', 11, 15, 0);
        }
        // Debug
        $this->SendDebug('ApplyChanges', 'state='.$state.' , region='.$region.' , days='.$days.', creates='.($hint ? 'Y' : 'N').'|'.($forecast ? 'Y' : 'N').'|'.($link ? 'Y' : 'N').', update='.($update ? 'Y' : 'N'), 0);
    }

    /**
     * Update a boolean value.
     *
     * @param string $Ident Ident of the boolean variable
     * @param bool   $value Value of the boolean variable
     */
    private function SetValueBoolean(string $ident, bool $value)
    {
        $id = $this->GetIDForIdent($ident);
        SetValueBoolean($id, $value);
    }

    /**
     * Update a string value.
     *
     * @param string $Ident Ident of the boolean variable
     * @param string $value Value of the string variable
     */
    private function SetValueString(string $ident, string $value)
    {
        $id = $this->GetIDForIdent($ident);
        SetValueString($id, $value);
    }

    /**
     * Update a integer value.
     *
     * @param string $Ident Ident of the boolean variable
     * @param int    $value Value of the string variable
     */
    private function SetValueInteger(string $ident, int $value)
    {
        $id = $this->GetIDForIdent($ident);
        SetValueInteger($id, $value);
    }

    /**
     * This function will be available automatically after the module is imported with the module control.
     * Using the custom prefix this function will be callable from PHP and JSON-RPC through:.
     *
     * ALMANAC_Update($id);
     */
    public function Update()
    {
        // Datenquelle 
        $json = file_get_contents(static::$dataURL); 
        // falls die Daten nicht geladen werden koennen 
        if (empty($json) || $json == "") { 
            $this->LogMessage('Fehler beim Auslesen des DWD Pollenflug-Gefahrenindex', KL_ERROR);
            return;
        }
        $data = json_decode($json, true);
        // Last Update
        if (array_key_exists('last_update', $data)) {
            $update = str_replace(' Uhr', '', $data['last_update']);
            $last = strtotime($update);
            $this->SetValueInteger('LastUpdate', $last); 
        }
        else {
            $this->LogMessage('Fehler beim Auslesen der letzten Aktualisierung');
            return;
        } 
        // Next Update
        if (array_key_exists('next_update', $data)) {
            $update = str_replace(' Uhr', '', $data['next_update']);
            $next = strtotime($update);
            $this->SetValueInteger('NextUpdate', $next);
        }
        else {
            $this->LogMessage('Fehler beim Auslesen der nächsten Aktualisierung');
            return;
        }
        // Daten raussuchen
        $state = $this->ReadPropertyInteger('State');
        $region = $this->ReadPropertyInteger('Region');
        $hint = $this->ReadPropertyBoolean('CreateHint');
        $forecast = $this->ReadPropertyBoolean('CreateForecast');
        // Suchen
        foreach ($data['content'] as $content) {
            if (($content['region_id'] == $state) && ($content['partregion_id'] == $region)) { 
                $pollen = $content['Pollen']; 
                // Neues Array mit meinen Pollenflugdaten aufbauen
                $pollination = array();
                foreach ($pollen as $key => $value) {
                    $pollination[$key] = array($value['today'], $value['tomorrow'],$value['dayafter_to']);
                }
                // Baue Html Tagestabelle ?
                if($forecast == true) {
                    BuildHtml($pollination, $last);
                }
                // Setze Tagesvorhersage ?
                if ($hint == true) {
                    BuildText($pollination);
                }
                break;
            } 
        } 
        // calculate next update interval
        $update = $this->ReadPropertyBoolean('DailyUpdate');
        if ($update == true) {
            $this->UpdateTimerInterval('UpdateTimer', 11, 15, 0);
        }
    }

    public function SelectState()
    {
        $this->UpdateFormField('Region', 'option', array());
    }

    private function BuildHtml($pollination, $time) {
        # Wochentage auf Deutsch
        $day = ['Sonntag', 'Montag', 'Dienstag', 'Mittwoch', 'Donnerstag', 'Freitag','Samstag'];
        # Übersetzungstabelle
        $trans = array('January'=>'Januar','February'=>'Februar','March'=>'März','May'=>'Mai','June'=>'Juni','July'=>'Juli','October'=>'Oktober','December'=>'Dezember');
        # Styles
        $style = "";
        $style = $style.'<style type="text/css">';
        # Erle (Alder)
        $style = $style.'div.erle {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Haselnuss (Hazelnut)
        $style = $style.'div.hasel {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Esche (Ash)
        $style = $style.'div.esche {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Birke (Birch)
        $style = $style.'div.birke {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Grässer (Grasses)
        $style = $style.'div.graeser {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        #Roggen (Rye)
        $style = $style.'div.roggen {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Beifuss
        $style = $style.'div.beifuss {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # Ambrosia
        $style = $style.'div.ambrosia {margin:8px;display: inline-block;width: 32px;height: 32px;background-image: url(data:image/svg+xml;utf8;base64,)}';
        # TABLE
        $style = $style.'table  {border-collapse: collapse; font-size: 14px; width: 100%; }';
        $style = $style.'td.fst {vertical-align: middle; text-align: center; padding: 5px 15px 5px 5px; border-left: 1px solid rgba(255, 255, 255, 0.2); border-top: 1px solid rgba(255, 255, 255, 0.1); }';
        $style = $style.'td.mid {vertical-align: middle; text-align: center; padding: 5px; border-top: 1px solid rgba(255, 255, 255, 0.1); border-left: 1px solid rgba(255, 255, 255, 0.2); border-right: 1px solid rgba(255, 255, 255, 0.2); }';
        $style = $style.'td.lst {vertical-align: middle; text-align: center; padding: 5px; border-right: 1px solid rgba(255, 255, 255, 0.2); border-top: 1px solid rgba(255, 255, 255, 0.1); }';
        $style = $style.'tr:last-child {border-bottom: 1px solid rgba(255, 255, 255, 0.2); }';
        $style = $style.'tr:nth-child(even) {  background-color: rgba(0, 0, 0, 0.2); }';	  
        $style = $style.'.th { color: rgb(255, 255, 255); background-color: rgb(160, 160, 0); font-weight:bold; background-image: linear-gradient(top,rgba(0,0,0,0) 0,rgba(0,0,0,0.3) 50%,rgba(0,0,0,0.3) 100%); background-image: -o-linear-gradient(top,rgba(0,0,0,0) 0,rgba(0,0,0,0.3) 50%,rgba(0,0,0,0.3) 100%); background-image: -moz-linear-gradient(top,rgba(0,0,0,0) 0,rgba(0,0,0,0.3) 50%,rgba(0,0,0,0.3) 100%); background-image: -webkit-linear-gradient(top,rgba(0,0,0,0) 0,rgba(0,0,0,0.3) 50%,rgba(0,0,0,0.3) 100%); background-image: -ms-linear-gradient(top,rgba(0,0,0,0) 0,rgba(0,0,0,0.3) 50%,rgba(0,0,0,0.3) 100%); }';
        $style = $style.'.num { font-size: 48px;color: rgb(160,160,0)}';
        $style = $style.'.mon { text-transform: uppercase; }';
        # SCALE
        $style = $style.'.scale {clear:both;display: inline-block;width: 8px;height: 8px;margin-left: 2px; border: solid 1px rgba(255, 255, 255, 0.2)}';
        $style = $style.'.square1 { background-color: darkgreen;}';
        $style = $style.'.square2 { background-color: green;}';
        $style = $style.'.square3 { background-color: greenyellow;}';
        $style = $style.'.square4 { background-color: gold;}';
        $style = $style.'.square5 { background-color: tomato;}';
        $style = $style.'.square6 { background-color: red;}';
        $style = $style.'.square7 { background-color: crimson;}';
        $style = $style.'</style>';
        // Tabelle bauen
        $html = $style;
        $html = $html.'<table>';
        // Header
        $html = $html.'<tr>';
        $html = $html."<td class='fst th'>Tag</td>";
        $size = sizeof($pollination);
        $index = 0;
        foreach($pollination as $key => $value) {
            if($index == $size-1) {
                $html = $html."<td class='lst th'>$key</td>";
            }
            else {
                $html = $html."<td class='mid th'>$key</td>";
            }
            $index++; 
        }
        $html = $html.'</tr>';
        // Datenzeilen
        $days = $this->ReadPropertyInteger('Days');
        $st = $time;
        for($i = 0; $i < $days; $i++) {
            // zeit
            $wd = $day[date("w", $st)];
            $md = date("j", $st);
            $mo = date("F", $st);
            $mo = strtr($mo, $trans);
            $item_day ='<div class=\'day\'>'.$wd.'</div><div class=\'num\'>'.$md.'</div><div class=\'mon\'>'.$mo.'</div>';
            // pollen
            $html = $html.'<tr><td class=\'fst\'>'.$item_day.'</td>';
            $index = 0;
            foreach($pollination as $key => $value) {
                if($index == $size-1) {
                    $html = $html."<td class='lst th'>$key</td>";
                }
                else {
                    $html = $html."<td class='mid'>".GetScale($key, $value[$i])."</td>";
                }
            }
            $html = $html.'</tr>';
            // Plus ein Tag
            $st = $st+ 86400;
        }
    
        $html = $html.'</table>';
        // HTML ausgeben
        $this->SetValueString('Forecast', $html);
    }
    
    /**
     * This function creats the textual summary of the forecast.
     */
    private function BuildText($pollination)
    {
        // Vorhersage für geringe Belastung
        $gb_text = '';
        // Vorhersage für mittlere Belastung
        $mb_text = '';
        // Vorhersage für hohe Belastung
        $hb_text = '';
        // Belastung durch ???
        foreach ($pollination as $key => $value) {
            switch ($value[0]) {
                case '0-1':
                case '1':
                    $gb_text = $gb_text . $key.' ';
                    break;
                case '1-2':
                case '2':
                    $mb_text = $mb_text . $key.' ';
                    break;
                case '2-3':
                case '3':
                    $hb_text = $hb_text . $key.' ';
                    break;
            }
        }
        // Ansagetext zusammen stellen
        if ($gb_text !== '') {
            $gb_text = 'Geringe Belastung durch ' . str_replace(' ', ', ', trim($gb_text).'.');
        }
        if ($mb_text !== '') {
            $mb_text = 'Mittlere Belastung durch ' . str_replace(' ', ', ', trim($mb_text).'.');
        }
        if ($hb_text !== '') {
            $hb_text = 'Hohe Belastung durch ' . str_replace(' ', ', ', trim($hb_text).'.');
        }
        $text = '';
        if ($gb_text !== '') {
            $text = trim($gb_text) . ' ' ;
        }
        if ($mb_text !== '') {
            $text = $text . trim($mb_text) . ' ';
        }
        if ($hb_text !== '') {
            $text = $text . trim($hb_text) . ' ';
        }
        // Nix los
        if ($text == '') {
            $text = 'Keine Belastung.';
        }
        // Text ausgeben
        $this->SetValueString('Hint', $text);
    }
    
    /**
     * This function translate a numeric scale values into text.
     */
    private function GetScale($type, $value)
    {
        # Level
        $asso = ['-1'=>0,'0'=>1,'0-1'=>2,'1'=>3,'1-2'=>4,'2'=>5,'2-3'=>6,'3'=>7];
        # Text
        $text = ['-1'=>'nicht bekannt','0'=>'keine','0-1'=>'keine bis gering','1'=>'gering','1-2'=>'gering bis mittel','2'=>'mittel','2-3'=>'mittel bis hoch','3'=>'hoch'];
        # Style class
        $class = strtolower($type);
        $level = $asso[$value];
        $div = '<div style=\'clear:both\'>'.$text[$value].'</div><div class=\''.$class.'\'></div><div>';
        for ($i = 1; $i <= 7; $i++) {
            if ($i <= $level) {
                $div = $div.'<span class=\'scale square'.$i.'\'></span>';
            } else {
                $div = $div.'<span class="scale"></span>';
            }
        }
        $div = $div.'</div>';
        return $div;
    }
}
